import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from '@aws-amplify/auth';
import HamburgerMenu from './HamburgerMenu';
import styles from './SendCoin.module.css';

export default function SendCoin() {
  const navigate = useNavigate();
  const [currentBalance, setCurrentBalance] = useState(0);
  const [totalSent, setTotalSent] = useState(0);
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleSignOut = async () => {
    try {
      setIsLoading(true);
      await signOut({ global: true });

      localStorage.clear();

      navigate('/', { replace: true });

      window.location.reload();
    } catch (error) {
      console.error('サインアウトエラー:', error);
      setError('サインアウト中にエラーが発生しました。');
    } finally {
      setIsLoading(false);
    }
  };

  const confirmSignOut = () => {
    if (window.confirm('本当にサインアウトしますか？')) {
      handleSignOut();
    }
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      if (parseInt(amount) <= 0) {
        setError('送金額は1コイン以上を指定してください');
        return;
      }

      if (parseInt(amount) > currentBalance) {
        setError('残高が不足しています');
        return;
      }

      console.log('送金処理:', { recipient, amount });

      setSuccess(`${recipient}さんに${amount}コインを送金しました！`);
      setCurrentBalance(prev => prev - parseInt(amount));
      setTotalSent(prev => prev + parseInt(amount));

      setAmount('');
      setRecipient('');
    } catch (error) {
      console.error('送金エラー:', error);
      setError('送金処理中にエラーが発生しました。');
    }
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <div className={styles.headerLeft}>
          <HamburgerMenu isOpen={isMenuOpen} toggleMenu={toggleMenu} />
          <h1 className={styles.headerTitle}>デジコイン送金</h1>
        </div>
        <button 
          onClick={confirmSignOut} 
          className={styles.signOutButton}
          disabled={isLoading}
        >
          {isLoading ? 'サインアウト中...' : 'サインアウト'}
        </button>
      </header>

      <div className={`${styles.menu} ${isMenuOpen ? styles.menuOpen : ''}`}>
        <div className={styles.menuHeader}>
          <h2 className={styles.menuTitle}>メニュー</h2>
        </div>
        <nav className={styles.nav}>
          <ul className={styles.menuList}>
            <li><a href="/send" className={styles.menuItem}>送金</a></li>
            <li><a href="/history" className={styles.menuItem}>取引履歴</a></li>
            <li><a href="/profile" className={styles.menuItem}>プロフィール</a></li>
          </ul>
        </nav>
      </div>

      {isMenuOpen && (
        <div className={styles.overlay} onClick={toggleMenu}></div>
      )}

      <div className={styles.card}>
        <div className={styles.balanceInfo}>
          <div className={styles.balance}>
            <h3>現在の残高</h3>
            <p className={styles.balanceAmount}>
              {currentBalance}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
          <div className={styles.balance}>
            <h3>合計送付枚数</h3>
            <p className={styles.balanceAmount}>
              {totalSent}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
        </div>
      </div>

      <div className={styles.card}>
        <h2 className={styles.title}>DigiCoin送付</h2>
        {error && <p className={styles.error}>{error}</p>}
        {success && <p className={styles.success}>{success}</p>}

        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label htmlFor="recipient" className={styles.label}>
              送金先ユーザー名:
            </label>
            <input
              type="text"
              id="recipient"
              value={recipient}
              onChange={(e) => setRecipient(e.target.value)}
              required
              className={styles.input}
            />
          </div>

          <div className={styles.formGroup}>
            <label htmlFor="amount" className={styles.label}>
              送金額 (コイン):
            </label>
            <input
              type="number"
              id="amount"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              required
              min="1"
              step="1"
              className={styles.input}
            />
          </div>

          <button type="submit" className={styles.button}>
            送金する
          </button>
        </form>
      </div>
    </div>
  );
}
