import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut } from '@aws-amplify/auth';
import HamburgerMenu from './HamburgerMenu';
import styles from './SendCoin.module.css';

export default function SendCoin() {
  const navigate = useNavigate();
  const [currentBalance, setCurrentBalance] = useState(0);
  const [totalSent, setTotalSent] = useState(0);
  const [recipient, setRecipient] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const handleSignOut = async () => {
    try {
      setIsLoading(true);
      await signOut({ global: true });
      localStorage.clear();
      navigate('/', { replace: true });
      window.location.reload();
    } catch (error) {
      console.error('サインアウトエラー:', error);
      setError('サインアウト中にエラーが発生しました。');
    } finally {
      setIsLoading(false);
    }
  };

  const confirmSignOut = () => {
    if (window.confirm('本当にサインアウトしますか？')) {
      handleSignOut();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      // 固定の送金額（例：1コイン）で送金処理
      console.log('送金処理:', { recipient });
      setSuccess(`${recipient}さんにコインを送金しました！`);
      setCurrentBalance(prev => prev - 1); // 1コイン固定
      setTotalSent(prev => prev + 1);
      setRecipient('');
    } catch (error) {
      console.error('送金エラー:', error);
      setError('送金処理中にエラーが発生しました。');
    }
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <div className={styles.headerLeft}>
          <HamburgerMenu isOpen={isMenuOpen} toggleMenu={() => setIsMenuOpen(!isMenuOpen)} />
          <h1 className={styles.headerTitle}>デジコイン送金</h1>
        </div>
        <button 
          onClick={confirmSignOut} 
          className={styles.signOutButton}
          disabled={isLoading}
        >
          {isLoading ? 'サインアウト中...' : 'サインアウト'}
        </button>
      </header>

      <div className={styles.card}>
        <div className={styles.balanceInfo}>
          <div className={styles.balance}>
            <h3>現在の残高</h3>
            <p className={styles.balanceAmount}>
              {currentBalance}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
          <div className={styles.balance}>
            <h3>合計送付枚数</h3>
            <p className={styles.balanceAmount}>
              {totalSent}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
        </div>
      </div>

      <div className={styles.card}>
        <h2 className={styles.title}>DigiCoin送付</h2>
        {error && <p className={styles.error}>{error}</p>}
        {success && <p className={styles.success}>{success}</p>}

        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label htmlFor="recipient" className={styles.label}>
              送金先ユーザー名:
            </label>
            <input
              type="text"
              id="recipient"
              value={recipient}
              onChange={(e) => setRecipient(e.target.value)}
              required
              className={styles.input}
            />
          </div>

          <button type="submit" className={styles.button}>
            送金する
          </button>
        </form>
      </div>
    </div>
  );
}
