import React, { useState, useEffect } from 'react';

export default function Ranking() {
  const [ranking, setRanking] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchRanking();
  }, []);

  async function fetchRanking() {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('認証トークンがありません');
      }

      const response = await fetch('/api/ranking', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        if (response.status === 404) {
          setRanking([]);
          return;
        }
        const errorData = await response.json();
        throw new Error(errorData.message || 'ランキングの取得に失敗しました');
      }

      const data = await response.json();
      if (!data.ranking || !Array.isArray(data.ranking)) {
        throw new Error('不正なランキングデータ形式です');
      }
      setRanking(data.ranking);
    } catch (err) {
      console.error('ランキング取得エラー:', err);
      setError('ランキングの取得に失敗しました: ' + err.message);
    }
  }

  return (
    <div className="container">
      <h2>受取残高ランキング</h2>
      {error && <p className="error">{error}</p>}
      {ranking.length > 0 ? (
        <ul className="ranking-list">
          {ranking.map((user, index) => (
            <li key={user._id} className="ranking-item">
              <span className="ranking-position">{index + 1}</span>
              <span className="ranking-username">{user.username}</span>
              <span className="ranking-balance">{user.receivedBalance} DGC</span>
            </li>
          ))}
        </ul>
      ) : (
        <p>ランキングデータがありません。</p>
      )}
    </div>
  );
}
