import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function LoginForm() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [affiliationCode, setAffiliationCode] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    const url = isLogin ? '/api/login' : '/api/register';

    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(isLogin ? { username, password } : { username, password, affiliationCode }),
      });

      const contentType = response.headers.get("content-type");
      if (contentType && contentType.indexOf("application/json") !== -1) {
        const data = await response.json();
        if (!response.ok) {
          throw new Error(data.message || 'エラーが発生しました');
        }

        if (isLogin) {
          localStorage.setItem('token', data.token);
          navigate('/send');
        } else {
          setIsLogin(true);
          setError('登録が完了しました。ログインしてください。');
        }
      } else {
        const text = await response.text();
        console.error('Expected JSON response but got:', text);
        throw new Error('サーバーからの応答が不正です');
      }
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'リクエストの処理中にエラーが発生しました');
    }
  };

  return (
    <div className="container">
      <div className="form-container">
        <h1>{isLogin ? 'ログイン' : '新規登録'}</h1>
        {error && <p className="error">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">ユーザー名:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">パスワード:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          {!isLogin && (
            <div className="form-group">
              <label htmlFor="affiliationCode">所属コード:</label>
              <input
                type="text"
                id="affiliationCode"
                value={affiliationCode}
                onChange={(e) => setAffiliationCode(e.target.value)}
                required
              />
            </div>
          )}
          <button type="submit" className="submit-button">
            {isLogin ? 'ログイン' : '登録'}
          </button>
        </form>
        <button
          className="toggle-button"
          onClick={() => setIsLogin(!isLogin)}
        >
          {isLogin ? '新規登録はこちら' : 'ログインはこちら'}
        </button>
      </div>
    </div>
  );
}
