import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import { Amplify } from 'aws-amplify';
import { cognitoUserPoolsTokenProvider } from '@aws-amplify/auth/cognito';

// デバッグ用の環境変数チェック
const envVars = {
  region: process.env.REACT_APP_REGION,
  userPoolId: process.env.REACT_APP_USER_POOL_ID,
  userPoolClientId: process.env.REACT_APP_USER_POOL_CLIENT_ID,
  apiEndpoint: process.env.REACT_APP_API_ENDPOINT
};

// 環境変数の存在チェック
Object.entries(envVars).forEach(([key, value]) => {
  if (!value) {
    throw new Error(`必要な環境変数が設定されていません: ${key}`);
  }
});

console.log('Amplify設定値:', envVars);

// トークンプロバイダーの設定
cognitoUserPoolsTokenProvider.setKeyValueStorage({
  storage: window.localStorage
});

// 認証設定を適用
Amplify.configure({
  Auth: {
    userPoolId: 'ap-northeast-1_ATf3lqfW1',
    userPoolWebClientId: '39p229mfp8p85cokfubk6p6c3r',
    region: 'ap-northeast-1'
  }
});

// API設定を適用
Amplify.configure({
  API: {
    endpoints: [
      {
        name: 'api',
        endpoint: 'https://kc16xo1fe3.execute-api.ap-northeast-1.amazonaws.com/dev',
        region: 'ap-northeast-1'
      }
    ]
  }
});

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
