import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signIn, signUp } from '@aws-amplify/auth';
import styles from './LoginForm.module.css';

export default function LoginForm({ setIsLoggedIn }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      if (isLogin) {
        // ログイン処理
        const user = await signIn({ username, password });
        console.log('ログイン成功:', user);
        setIsLoggedIn(true);
        navigate('/send');
      } else {
        // 新規登録処理
        await signUp({
          username,
          password,
          attributes: {
            // 必要に応じて追加の属性を設定
            // email: email,
            // custom:affiliationCode: affiliationCode
          }
        });
        setIsLogin(true);
        setError('登録が完了しました。ログインしてください。');
      }
    } catch (error) {
      console.error('認証エラー:', error);
      setError(error.message || '認証処理中にエラーが発生しました');
    }
  };

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        <h1 className={styles.title}>{isLogin ? 'ログイン' : '新規登録'}</h1>
        {error && <p className={styles.error}>{error}</p>}
        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label htmlFor="username" className={styles.label}>ユーザー名:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              className={styles.input}
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="password" className={styles.label}>パスワード:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className={styles.input}
            />
          </div>
          <button type="submit" className={styles.submitButton}>
            {isLogin ? 'ログイン' : '登録'}
          </button>
        </form>
        <button
          className={styles.toggleButton}
          onClick={() => setIsLogin(!isLogin)}
        >
          {isLogin ? '新規登録はこちら' : 'ログインはこちら'}
        </button>
      </div>
    </div>
  );
}
