import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut, getCurrentUser, fetchAuthSession } from '@aws-amplify/auth';
import { get, post } from '@aws-amplify/api';
import HamburgerMenu from './HamburgerMenu';
import styles from './SendCoin.module.css';

export default function SendCoin() {
  const navigate = useNavigate();
  const [currentBalance, setCurrentBalance] = useState(0);
  const [totalSent, setTotalSent] = useState(0);
  const [recipient, setRecipient] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [transactions, setTransactions] = useState([]);
  const [currentUserInfo, setCurrentUserInfo] = useState(null);

  // ユーザー情報の取得
  const fetchCurrentUser = async () => {
    try {
      const user = await getCurrentUser();
      setCurrentUserInfo(user);
    } catch (error) {
      console.error('ユーザー情報の取得エラー:', error);
    }
  };

  // ユーザー情報の取得
  const fetchUserData = async () => {
    let session;
    try {
      session = await fetchAuthSession();
      console.log('認証セッション:', session);

      if (!session?.tokens?.idToken) {
        throw new Error('認証トークンが見つかりません');
      }

      const token = session.tokens.idToken.toString();
      const userId = session.userSub;

      console.log('リクエストユーザーID:', userId);

      // APIリクエストの詳細をログ出力
      console.log('APIリクエスト詳細:', {
        path: '/user',
        queryParams: { username: userId },
        token: token.substring(0, 20) + '...' // トークンの一部のみ表示
      });

      const apiResponse = await get({
        apiName: 'digicoin',
        path: '/user',
        options: {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          queryParams: {
            username: userId
          }
        }
      }).response;

      console.log('APIレスポンス:', apiResponse); // レスポンスオブジェクトを確認

      const userData = await apiResponse.json();
      console.log('生のユーザーデータ:', userData);

      if (userData) {
        console.log('設定する値:', {
          balance: Number(userData.balance),
          total: Number(userData.total),
          userId: userData.userId,
          name: userData.name
        });

        // 値を設定する前に型変換を確実に行う
        const balance = Number(userData.balance) || 0;
        const total = Number(userData.total) || 0;

        setCurrentBalance(balance);
        setTotalSent(total);
        setCurrentUserInfo({
          userId: userData.userId,
          username: userData.name
        });

        // 即座に状態を確認するために setTimeout を使用
        setTimeout(() => {
          console.log('更新後の状態:', {
            currentBalance,
            totalSent,
            currentUserInfo
          });
        }, 0);
      }

    } catch (error) {
      console.error('詳細なエラー情報:', {
        error: error,
        message: error.message,
        name: error.name,
        stack: error.stack
      });
      setError('ユーザー情報の取得に失敗しました');
    }
  };

  // 取引履歴の取得
  const fetchTransactions = async () => {
    try {
      const session = await fetchAuthSession();
      if (!session?.tokens?.idToken) {
        throw new Error('認証トークンが見つかりません');
      }

      const token = session.tokens.idToken.toString();
      const username = session.tokens.idToken.payload['cognito:username'];

      const apiResponse = await get({
        apiName: 'digicoin',
        path: '/transactions',
        options: {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          queryParams: {
            username: username
          },
          withCredentials: true
        }
      });

      // response プロパティを await で解決
      const response = await apiResponse.response;
      console.log('解決済み取引履歴レスポンス:', response);

      const transactionData = await response.json();
      console.log('取引履歴データ:', transactionData);

      setTransactions(transactionData);

    } catch (error) {
      console.error('取引履歴の取得エラー:', {
        error: error,
        message: error.message,
        stack: error.stack
      });
      setError('取引履歴の取得に失敗しました');
    }
  };

  // コンポーネントのマウント時にデータを取得
  useEffect(() => {
    const loadData = async () => {
      try {
        await fetchUserData();
        await fetchTransactions();
      } catch (error) {
        console.error('データ読み込みエラー:', error);
      }
    };

    loadData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    try {
      const session = await fetchAuthSession();
      const token = session.tokens.idToken.toString();

      await post({
        apiName: 'digicoin',
        path: '/send',
        options: {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          body: {
            recipientUsername: recipient,
            senderUsername: session.tokens.idToken.payload['cognito:username']
          }
        }
      });

      setSuccess(`${recipient}さんにコインを送金しました！`);
      setRecipient('');

      await fetchUserData();
      await fetchTransactions();
    } catch (error) {
      console.error('送金エラー:', error);
      setError('送金処理中にエラーが発生しました。');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignOut = async () => {
    try {
      setIsLoading(true);
      await signOut({ global: true });
      localStorage.clear();
      navigate('/', { replace: true });
      window.location.reload();
    } catch (error) {
      console.error('サインアウトエラー:', error);
      setError('サインアウト中にエラーが発生しました。');
    } finally {
      setIsLoading(false);
    }
  };

  const confirmSignOut = () => {
    if (window.confirm('本当にサインアウトしますか？')) {
      handleSignOut();
    }
  };

  // 取引履歴の表示用関数
  const renderTransactionHistory = () => {
    return transactions.map((tx) => (
      <div key={tx.transactionId} className={styles.transaction}>
        <div className={styles.transactionType}>
          {tx.type === 'sent' ? '送金' : '受取'}
        </div>
        <div className={styles.transactionDetails}>
          <span className={styles.partner}>
            {tx.type === 'sent' ? `送金先: ${tx.partnerId}` : `送金元: ${tx.partnerId}`}
          </span>
          <span className={styles.timestamp}>
            {new Date(tx.timestamp).toLocaleString()}
          </span>
        </div>
      </div>
    ));
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <div className={styles.headerLeft}>
          <HamburgerMenu isOpen={isMenuOpen} toggleMenu={() => setIsMenuOpen(!isMenuOpen)} />
          <h1 className={styles.headerTitle}>デジコイン送金</h1>
        </div>
        <button 
          onClick={confirmSignOut} 
          className={styles.signOutButton}
          disabled={isLoading}
        >
          {isLoading ? 'サインアウト中...' : 'サインアウト'}
        </button>
      </header>

      <div className={styles.card}>
        <div className={styles.userInfo}>
          <h3>ログイン情報</h3>
          <p className={styles.userDetail}>
            <span className={styles.userLabel}>ユーザーID:</span>
            <span className={styles.userId}>{currentUserInfo?.userId}</span>
          </p>
          <p className={styles.userDetail}>
            <span className={styles.userLabel}>ユーザー名:</span>
            <span className={styles.username}>{currentUserInfo?.username}</span>
          </p>
        </div>
        <div className={styles.balanceInfo}>
          <div className={styles.balance}>
            <h3>現在の残高</h3>
            <p className={styles.balanceAmount}>
              {currentBalance}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
          <div className={styles.balance}>
            <h3>合計送付枚数</h3>
            <p className={styles.balanceAmount}>
              {totalSent}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
        </div>
      </div>

      <div className={styles.card}>
        <h2 className={styles.title}>DigiCoin送付</h2>
        {error && <p className={styles.error}>{error}</p>}
        {success && <p className={styles.success}>{success}</p>}

        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label htmlFor="recipient" className={styles.label}>
              送金先ユーザー名:
            </label>
            <input
              type="text"
              id="recipient"
              value={recipient}
              onChange={(e) => setRecipient(e.target.value)}
              required
              className={styles.input}
              disabled={isLoading}
            />
          </div>

          <button 
            type="submit" 
            className={styles.button}
            disabled={isLoading}
          >
            {isLoading ? '送金中...' : '送金する'}
          </button>
        </form>
      </div>

      <div className={styles.card}>
        <h2 className={styles.title}>取引履歴</h2>
        <div className={styles.transactionList}>
          {renderTransactionHistory()}
        </div>
      </div>
    </div>
  );
}
