import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';
import './App.css';
import LoginForm from './components/LoginForm';
import SendCoin from './components/SendCoin';
import Ranking from './components/Ranking';
import AllHistory from './components/AllHistory';
import TransactionInfo from './components/TransactionInfo';
import PrivateRoute from './components/PrivateRoute';
// Profileのインポートを削除

function NavBar() {
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const handleLogoClick = (e) => {
    e.preventDefault();
    if (isLoggedIn) {
      navigate('/send');
    } else {
      navigate('/');
    }
  };

  return (
    <nav className="navbar">
      <a href="/" onClick={handleLogoClick} className="nav-logo">DigiCoin</a>
      {isLoggedIn && (
        <div className="nav-links">
          <Link to="/send">コイン送付</Link>
          <Link to="/ranking">ランキング</Link>
          <Link to="/history">全履歴</Link>
          <button onClick={handleLogout} className="logout-button">ログアウト</button>
        </div>
      )}
    </nav>
  );
}

function AppContent() {
  return (
    <div className="app">
      <NavBar />
      <Routes>
        <Route path="/" element={<LoginForm />} />
        <Route path="/send" element={<PrivateRoute><SendCoin /></PrivateRoute>} />
        <Route path="/ranking" element={<PrivateRoute><Ranking /></PrivateRoute>} />
        <Route path="/history" element={<PrivateRoute><AllHistory /></PrivateRoute>} />
        <Route path="/transaction/:id" element={<PrivateRoute><TransactionInfo /></PrivateRoute>} />
        {/* プロフィールのルートを削除 */}
      </Routes>
    </div>
  );
}

function App() {
  return (
    <Router>
      <AppContent />
    </Router>
  );
}

export default App;
