import { MAX_DELAY_MS } from './constants';
import { jitteredBackoff } from './jitteredBackoff';
import { retry } from './retry';
/**
 * @private
 * Internal use of Amplify only
 */
export const jitteredExponentialRetry = (functionToRetry, args, maxDelayMs = MAX_DELAY_MS, onTerminate) => retry(functionToRetry, args, jitteredBackoff(maxDelayMs), onTerminate);
