import React, { useState } from 'react';

function TransactionInfo() {
  const [address, setAddress] = useState('');
  const [transactionInfo, setTransactionInfo] = useState(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    // ここでEthereumのトランザクション情報を取得するロジックを実装
    // ダミーデータを使用
    setTransactionInfo({
      from: '0x1234...',
      to: '0x5678...',
      amount: 10,
      date: '2023-05-04 12:34:56',
    });
  };

  return (
    <div className="container">
      <h2>取引情報</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="address">Ethereumアドレス</label>
          <input
            id="address"
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="submit-button">情報を取得</button>
      </form>
      {transactionInfo && (
        <div>
          <h3>取引詳細</h3>
          <p>送信元: {transactionInfo.from}</p>
          <p>送信先: {transactionInfo.to}</p>
          <p>金額: {transactionInfo.amount}</p>
          <p>日時: {transactionInfo.date}</p>
        </div>
      )}
    </div>
  );
}

export default TransactionInfo;
