import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { signIn, signUp, signOut, confirmSignIn } from '@aws-amplify/auth';
import styles from './LoginForm.module.css';

export default function LoginForm({ setIsLoggedIn }) {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [error, setError] = useState('');
  const [isLogin, setIsLogin] = useState(true);
  const [showNewPasswordForm, setShowNewPasswordForm] = useState(false);
  const [tempUser, setTempUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const clearSession = async () => {
      try {
        await signOut();
        setIsLoggedIn(false);
      } catch (error) {
        console.error('サインアウトエラー:', error);
      }
    };
    clearSession();
  }, [setIsLoggedIn]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      if (isLogin) {
        const user = await signIn({
          username,
          password,
          options: {
            authFlowType: 'USER_PASSWORD_AUTH'
          }
        });

        if (user.isSignedIn) {
          setIsLoggedIn(true);
          navigate('/send');
        }
      } else {
        // 新規登録処理
        await signUp({
          username,
          password,
          options: {
            authFlowType: 'USER_PASSWORD_AUTH'
          }
        });
        setError('登録が完了しました。ログインしてください。');
        setIsLogin(true);
      }
    } catch (error) {
      console.error('認証エラー:', error);
      if (error.name === 'NotAuthorizedException') {
        setError('ユーザー名またはパスワードが正しくありません。');
      } else if (error.name === 'UserNotFoundException') {
        setError('ユーザーが見つかりません。');
      } else if (error.name === 'UserNotConfirmedException') {
        setError('ユーザーの確認が完了していません。');
      } else {
        setError('認証中にエラーが発生しました。もう一度お試しください。');
      }
    }
  };

  const handleNewPasswordSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const result = await confirmSignIn({
        challengeResponse: newPassword,
        user: tempUser
      });

      if (result.isSignedIn) {
        setIsLoggedIn(true);
        navigate('/send');
      } else {
        setError('ログインに失敗しました。もう一度お試しください。');
      }
    } catch (error) {
      console.error('パスワード更新エラー:', error);
      setError(error.message || 'パスワードの更新中にエラーが発生しました');
    }
  };

  if (showNewPasswordForm) {
    return (
      <div className={styles.container}>
        <div className={styles.formContainer}>
          <h1 className={styles.title}>新しいパスワードを設定</h1>
          {error && <p className={styles.error}>{error}</p>}
          <form onSubmit={handleNewPasswordSubmit} className={styles.form}>
            <div className={styles.formGroup}>
              <label htmlFor="newPassword" className={styles.label}>新しいパスワード:</label>
              <input
                type="password"
                id="newPassword"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
                className={styles.input}
                autoComplete="new-password"
              />
            </div>
            <button type="submit" className={styles.submitButton}>
              パスワードを更新
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className={styles.container}>
      <div className={styles.formContainer}>
        <h1 className={styles.title}>{isLogin ? 'ログイン' : '新規登録'}</h1>
        {error && <p className={styles.error}>{error}</p>}
        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label htmlFor="username" className={styles.label}>ユーザー名:</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              required
              className={styles.input}
              autoComplete="username"
            />
          </div>
          <div className={styles.formGroup}>
            <label htmlFor="password" className={styles.label}>パスワード:</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className={styles.input}
              autoComplete={isLogin ? "current-password" : "new-password"}
            />
          </div>
          <button type="submit" className={styles.submitButton}>
            {isLogin ? 'ログイン' : '登録'}
          </button>
          <button
            type="button"
            onClick={() => setIsLogin(!isLogin)}
            className={styles.toggleButton}
          >
            {isLogin ? '新規登録はこちら' : 'ログインはこちら'}
          </button>
        </form>
      </div>
    </div>
  );
}
