import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { signOut, getCurrentUser, fetchAuthSession } from '@aws-amplify/auth';
import { get, post } from '@aws-amplify/api';
import HamburgerMenu from './HamburgerMenu';
import styles from './SendCoin.module.css';

export default function SendCoin() {
  const navigate = useNavigate();
  const [currentBalance, setCurrentBalance] = useState(0);
  const [totalSent, setTotalSent] = useState(0);
  const [recipient, setRecipient] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [transactions, setTransactions] = useState([]);
  const [currentUserInfo, setCurrentUserInfo] = useState(null);
  const [userList, setUserList] = useState([]);

  // ユーザー情報の取得
  const fetchCurrentUser = async () => {
    try {
      const user = await getCurrentUser();
      setCurrentUserInfo(user);
    } catch (error) {
      console.error('ユーザー情報の取得エラー:', error);
    }
  };

  // ユーザー情報と全ユーザー名の取得
  const fetchUserData = async () => {
    try {
      const session = await fetchAuthSession();
      const token = session.tokens.idToken.toString();

      const response = await get({
        apiName: 'digicoin',
        path: '/api/user', // エンドポイントを設定
        options: {
          headers: {
            Authorization: `Bearer ${token}`
          }
        }
      });

      const result = await response.response;
      const { userInfo, allUserNames } = result.body;

      if (userInfo) {
        setCurrentUserInfo({
          userId: userInfo.userId,
          username: userInfo.name
        });
        setCurrentBalance(userInfo.balance);
        setTotalSent(userInfo.total);
      }

      if (Array.isArray(allUserNames)) {
        setUserList(allUserNames);
      }

    } catch (error) {
      console.error('エラーの詳細:', error);
      setError('ユーザー情報の取得に失敗しました');
    }
  };

  // 取引履歴の取得
  const fetchTransactions = async () => {
    try {
      const session = await fetchAuthSession();
      console.log('認証セッション:', session);

      if (!session?.tokens?.idToken) {
        throw new Error('認証トークンが見つかりません');
      }

      const token = session.tokens.idToken.toString();
      const userId = session.userSub;

      const response = await get({
        apiName: 'digicoin',
        path: '/api/transactions',
        options: {
          headers: {
            Authorization: `Bearer ${token}`
          },
          queryParams: {
            userId: userId
          }
        }
      });

      // レスポンスの待機
      const result = await response.response;
      console.log('取引履歴結果:', result);

      if (result.body) {
        setTransactions(result.body);
      }

    } catch (error) {
      console.error('取引履歴の取得エラー:', error);
      setError('取引履歴の取得に失敗しました');
    }
  };

  // コンポーネントのマウント時にデータを取得
  useEffect(() => {
    const loadData = async () => {
      try {
        await fetchUserData();
        await fetchTransactions();
      } catch (error) {
        console.error('データ読み込みエラー:', error);
      }
    };

    loadData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsLoading(true);

    try {
      const session = await fetchAuthSession();
      const token = session.tokens.idToken.toString();

      await post({
        apiName: 'digicoin',
        path: '/send',
        options: {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          },
          body: {
            recipientUsername: recipient,
            senderUsername: session.tokens.idToken.payload['cognito:username']
          }
        }
      });

      setSuccess(`${recipient}さんにコインを送金しました！`);
      setRecipient('');

      await fetchUserData();
      await fetchTransactions();
    } catch (error) {
      console.error('送金エラー:', error);
      setError('送金処理中にエラーが発生しました。');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignOut = async () => {
    try {
      setIsLoading(true);
      await signOut({ global: true });
      localStorage.clear();
      navigate('/', { replace: true });
      window.location.reload();
    } catch (error) {
      console.error('サインアウトエラー:', error);
      setError('サインアウト中にエラーが発生しました。');
    } finally {
      setIsLoading(false);
    }
  };

  const confirmSignOut = () => {
    if (window.confirm('本当にサインアウトしますか？')) {
      handleSignOut();
    }
  };

  // 取引履歴の表示用関数
  const renderTransactionHistory = () => {
    return transactions.map((tx) => (
      <div key={tx.transactionId} className={styles.transaction}>
        <div className={styles.transactionType}>
          {tx.type === 'sent' ? '送金' : '受取'}
        </div>
        <div className={styles.transactionDetails}>
          <span className={styles.partner}>
            {tx.type === 'sent' ? `送金先: ${tx.partnerId}` : `送金元: ${tx.partnerId}`}
          </span>
          <span className={styles.timestamp}>
            {new Date(tx.timestamp).toLocaleString()}
          </span>
        </div>
      </div>
    ));
  };

  return (
    <div className={styles.container}>
      <header className={styles.header}>
        <div className={styles.headerLeft}>
          <HamburgerMenu isOpen={isMenuOpen} toggleMenu={() => setIsMenuOpen(!isMenuOpen)} />
          <h1 className={styles.headerTitle}>デジコイン送金</h1>
        </div>
        <button 
          onClick={confirmSignOut} 
          className={styles.signOutButton}
          disabled={isLoading}
        >
          {isLoading ? 'サインアウト中...' : 'サインアウト'}
        </button>
      </header>

      <div className={styles.card}>
        <div className={styles.userInfo}>
          <h3>ログイン情報</h3>
          <p className={styles.userDetail}>
            <span className={styles.userLabel}>ユーザーID:</span>
            <span className={styles.userId}>{currentUserInfo?.userId}</span>
          </p>
          <p className={styles.userDetail}>
            <span className={styles.userLabel}>ユーザー名:</span>
            <span className={styles.username}>{currentUserInfo?.username}</span>
          </p>
        </div>
        <div className={styles.balanceInfo}>
          <div className={styles.balance}>
            <h3>現在の残高</h3>
            <p className={styles.balanceAmount}>
              {currentBalance}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
          <div className={styles.balance}>
            <h3>合計送付枚数</h3>
            <p className={styles.balanceAmount}>
              {totalSent}
              <span className={styles.currency}>DGC</span>
            </p>
          </div>
        </div>
      </div>

      <div className={styles.card}>
        <h2 className={styles.title}>DigiCoin送付</h2>
        {error && <p className={styles.error}>{error}</p>}
        {success && <p className={styles.success}>{success}</p>}

        <form onSubmit={handleSubmit} className={styles.form}>
          <div className={styles.formGroup}>
            <label htmlFor="recipient" className={styles.label}>
              送金先ユーザー名:
            </label>
            <select
              id="recipient"
              value={recipient}
              onChange={(e) => setRecipient(e.target.value)}
              required
              className={styles.input}
              disabled={isLoading}
            >
              <option value="">選択してください</option>
              {userList.map(name => (
                <option key={name} value={name}>
                  {name}
                </option>
              ))}
            </select>
          </div>

          <button 
            type="submit" 
            className={styles.button}
            disabled={isLoading}
          >
            {isLoading ? '送金中...' : '送金する'}
          </button>
        </form>
      </div>

      <div className={styles.card}>
        <h2 className={styles.title}>取引履歴</h2>
        <div className={styles.transactionList}>
          {renderTransactionHistory()}
        </div>
      </div>
    </div>
  );
}
