import React, { useState, useEffect } from 'react';

export default function SendCoin() {
  const [recipient, setRecipient] = useState('');
  const [amount, setAmount] = useState('');
  const [balance, setBalance] = useState(0);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchBalance();
    fetchUsers();
  }, []);

  async function fetchBalance() {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/balance', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (!response.ok) {
        throw new Error('残高の取得に失敗しました');
      }
      const data = await response.json();
      setBalance(data.balance);
    } catch (err) {
      console.error('残高取得エラー:', err);
      setError('残高の取得に失敗しました。');
    }
  }

  async function fetchUsers() {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/users', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      if (!response.ok) {
        throw new Error('ユーザーリストの取得に失敗しました');
      }
      const data = await response.json();
      setUsers(data.users);
    } catch (err) {
      console.error('ユーザーリスト取得エラー:', err);
      setError('ユーザーリストの取得に失敗しました。');
    }
  }

  async function handleSubmit(e) {
    e.preventDefault();
    setError('');
    setSuccess('');

    if (!recipient || !amount) {
      setError('受取人と枚数を入力してください。');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/send', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ recipient, amount })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || '送付に失敗しました');
      }

      const data = await response.json();
      setSuccess('送付が完了しました！');
      setBalance(data.newBalance);
      setRecipient('');
      setAmount('');
    } catch (err) {
      console.error('送付エラー:', err);
      setError(err.message || '送付に失敗しました。残高が十分かご確認ください。');
    }
  }

  return (
    <div className="container">
      <h2>DigiCoin送付</h2>
      <p>残高: {balance} DGC</p>
      {error && <p className="error">{error}</p>}
      {success && <p className="success">{success}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="recipient">受取人:</label>
          <select
            id="recipient"
            value={recipient}
            onChange={(e) => setRecipient(e.target.value)}
            required
          >
            <option value="">選択してください</option>
            {users.map(user => (
              <option key={user} value={user}>{user}</option>
            ))}
          </select>
        </div>
        <div>
          <label htmlFor="amount">枚数:</label>
          <input
            type="number"
            id="amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
            min="1"
            step="1"
          />
        </div>
        <button type="submit">送付</button>
      </form>
    </div>
  );
}
